import React, { useState } from 'react';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);

  const handleDemoLogin = () => {
    setUser({ name: 'Demo User', plan: 'FREE', credits: 45 });
    setIsLoggedIn(true);
  };

  return (
    <div className="App">
      <header className="header">
        <div className="container">
          <div className="logo">
            <h1>🎥 AI Video Chat</h1>
            <p>Trasforma i tuoi video in conversazioni intelligenti</p>
          </div>
          <div className="auth-section">
            {isLoggedIn ? (
              <div className="user-info">
                <span>Benvenuto, {user?.name}!</span>
                <span className="plan">Piano {user?.plan} • {user?.credits} crediti rimanenti</span>
              </div>
            ) : (
              <button className="login-btn" onClick={handleDemoLogin}>
                Accedi Demo
              </button>
            )}
          </div>
        </div>
      </header>

      <main className="main">
        <div className="container">
          {!isLoggedIn ? (
            <div className="hero">
              <h2>Analizza i tuoi video con l'intelligenza artificiale</h2>
              <div className="features">
                <div className="feature">
                  <h3>🎯 Analisi Completa</h3>
                  <p>Trascrizione audio e descrizione delle scene</p>
                </div>
                <div className="feature">
                  <h3>💬 Chat Intelligente</h3>
                  <p>Fai domande naturali e ricevi risposte precise</p>
                </div>
                <div className="feature">
                  <h3>🤖 AI Avanzata</h3>
                  <p>Tecnologia multimodale per video e audio</p>
                </div>
              </div>
            </div>
          ) : (
            <div className="upload-section">
              <h2>Carica il tuo video</h2>
              <div className="upload-area">
                <div className="upload-box">
                  <p>📁 Trascina un video qui o clicca per selezionare</p>
                  <p className="upload-info">Supporta: MP4, AVI, MOV, MKV, WEBM (max 100MB)</p>
                  <button className="upload-btn">Seleziona Video</button>
                </div>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
